import React, { Component, useState } from "react";
import Link from "next/link";
import "../component/map/config";
import GooglePlacesAutocomplete, {
  geocodeByAddress,
  getLatLng,
} from "react-google-places-autocomplete";
import Custommap from "../component/custommap";

export default function App() {
  function clickSubmit(e) {
    {
      /* 
 global.config.place.deliver.pickoff = address.label;
    global.config.place.deliver.dropoff = addressDrop.label;

    global.config.place.deliver.pickofflat = coordinates.lat;
    global.config.place.deliver.pickofflang = coordinates.lng;

    global.config.place.deliver.dropofflat = coordinatesDrop.lat;
    global.config.place.deliver.dropofflang = coordinatesDrop.lng;
*/
    }
  }

  const [address, setAddress] = React.useState("");
  const [addressDrop, setAddressDrop] = React.useState("");
  const [coordinates, setCoordinates] = React.useState({
    lat: null,
    lng: null,
  });
  const [coordinatesDrop, setCoordinatesDrop] = React.useState({
    lat: null,
    lng: null,
  });

  const handleChange = async (value) => {
    const results = await geocodeByAddress(value.label);
    const latLng = await getLatLng(results[0]);
    setAddress(value);
    setCoordinates(latLng);
    clickSubmit();
  };

  const handleChangeDrop = async (value) => {
    const results = await geocodeByAddress(value.label);
    const latLng = await getLatLng(results[0]);
    setAddressDrop(value);
    setCoordinatesDrop(latLng);
  };

  return (
    <div className="col-lg-6 colDeliver">
      <p className="pTitle">Book your delivery now!</p>
      <div className="box">
        <div className="form-inline">
          <img src="Image/maps.png" className="img-fluid"  data-toggle="modal"
            data-target="#exampleModalCenter"></img>
          <GooglePlacesAutocomplete
            selectProps={{
              value: address,
              onChange: handleChange,
            }}
            autocompletionRequest={{
              componentRestrictions: {
                country: ["ph"],
              },
            }}
          />
        </div>
        <div className="form-inline">
          <img src="Image/maps.png" className="img-fluid"></img>
          <GooglePlacesAutocomplete
            selectProps={{
              value: addressDrop,
              onChange: handleChangeDrop,
            }}
            autocompletionRequest={{
              componentRestrictions: {
                country: ["ph"],
              },
            }}
          />
        </div>
      </div>

      <Link href="/">
        <input
          type="button"
          className="btnSubmit"
          value="SUBMIT"
          onClick={clickSubmit}
        ></input>
      </Link>
      <div
        className="modal fade"
        id="exampleModalCenter"
        tabIndex={-1}
        role="dialog"
        aria-labelledby="exampleModalCenterTitle"
        aria-hidden="true"
      >
        <div
          className="modal-dialog modal-dialog-centered modal-lg"
          role="document"
        >
          <div className="modal-content">
            <div className="modal-body">
              <Custommap></Custommap>
              <Link href="/">
                <button onClick={getAdd} data-dismiss="modal">
                  set
                </button>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
    
  );
}
