import React, { Component } from "react";

export class login extends Component {
  constructor() {
    super();

    this.state = {
      Email: "",
      Passoword: "",
    };

    this.Password = this.Password.bind(this);
    14;
    this.Email = this.Email.bind(this);
    15;
    this.login = this.login.bind(this);
  }

  Email(event) {
    this.setState({ Email: event.target.value });
  }

  Password(event) {
    this.setState({ Password: event.target.value });
  }

  login(event) {
    fetch("http://localhost:8000/api/auth/login", {
      method: "post",
      headers: {
        Accept: "*/*",
        "Content-Type": "application/json",
      },

      body: JSON.stringify({
        Email: this.state.Email,
        Password: this.state.Password,
      }),
    })
      .then((Response) => Response.json())
      .then((result) => {
        console.log(result);
        if (result.status === "fail") alert("Invalid User");
        else alert("tama");
      });
  }

  render() {
    return (
      <div className="col-lg-6 colLogin">
        <p className="pLogin">Log-in</p>
        <div className="boxLogin">
          <input
            type="text"
            className="txt"
            placeholder="email"
            onChange={this.Email}
          ></input>
          <input
            type="password"
            className="txt"
            placeholder="password"
            onChange={this.Password}
          ></input>
        </div>
        <div className="row align-items-center">
          <div className="col-lg-6 text-center">
            <p className="pForgot">forgot password?</p>
          </div>
          <div className="col-lg-6 text-center">
            <button className="btnSubmit" onClick={this.login}>
              SUBMIT
            </button>
          </div>
        </div>
        <div className="row">
          <div className="col-lg-5">
            <hr></hr>
          </div>
          <div className="col-lg-2 text-center">
            <p className="pOr">or</p>
          </div>
          <div className="col-lg-5">
            <hr></hr>
          </div>
        </div>
        <button className="btnFacebook">
          <img
            src="Image/facebook.png"
            style={{ width: "15px", marginRight: "5px" }}
          />
          Sign-in with facebook
        </button>
        <button className="btnGoogle">
          <img
            src="Image/google.png"
            style={{ width: "15px", marginRight: "5px" }}
          />
          Sign-in with Google
        </button>
        <p className="pDont">Don't have an account?</p>
        <p className="pSignup">sign-up</p>
      </div>
    );
  }
}

export default login;
