import React, { Component } from "react";
import Googlemap from "../component/map/maploader";
import Header from "../component/header";
import "../component/map/config";
import PlacesAutocomplete, {
  geocodeByAddress,
  getLatLng,
} from "react-places-autocomplete";
import Componentdidmount from "../component/componentdidmount";

export default function map() {
  const searchOptions = {
    componentRestrictions: { country: ["ph"] },
  };

  const [address, setAddress] = React.useState("");
  const [addressDrop, setAddressDrop] = React.useState("");
  const [coordinates, setCoordinates] = React.useState({
    lat: null,
    lng: null,
  });
  const [coordinatesDrop, setCoordinatesDrop] = React.useState({
    lat: null,
    lng: null,
  });

  const handleSelect = async (value) => {
    const results = await geocodeByAddress(value);
    const latLng = await getLatLng(results[0]);
    setAddress(value);
    setCoordinates(latLng);
  };

  const handleSelectDrop = async (value) => {
    const results = await geocodeByAddress(value);
    const latLng = await getLatLng(results[0]);
    setAddressDrop(value);
    setCoordinatesDrop(latLng);
  };

  return (
    <div className="container-fluid conMap h-100">
      <Componentdidmount></Componentdidmount>
      <Header></Header>
      <div className="row align-items-center h-100">
        <div className="col-lg-6 colLeft">
          <p className="pPick">
            {" "}
            <img
              src="Image/mapgps.svg"
              className="img-fluid"
              style={{ marginRight: "10px" }}
            ></img>{" "}
            Pick Up Location
          </p>
          <input
            type="text"
            className="txtPickup"
            defaultValue={global.config.place.deliver.pickoff}
          ></input>
          <PlacesAutocomplete
            value={address}
            onChange={setAddress}
            onSelect={handleSelect}
            searchOptions={searchOptions}
          >
            {({
              getInputProps,
              suggestions,
              getSuggestionItemProps,
              loading,
            }) => (
              <div>
                <input defaultValue={global.config.place.deliver.pickoff}
                  {...getInputProps({
                    placeholder: "Type address",
                    className: "location-search-input-map",
                    
                  })}
                />

                <div className="autocomplete-dropdown-container">
                  {loading && <div>Loading...</div>}
                  {suggestions.map((suggestion) => {
                    const className = suggestion.active
                      ? "suggestion-item--active"
                      : "suggestion-item";
                    // inline style for demonstration purpose
                    const style = suggestion.active
                      ? {
                          backgroundColor: "#fafafa",
                          cursor: "pointer",
                        }
                      : {
                          backgroundColor: "#ffffff",
                          cursor: "pointer",
                        };
                    return (
                      <div
                        {...getSuggestionItemProps(suggestion, {
                          className: "input-suggestion",
                          style,
                        })}
                      >
                        <span>{suggestion.description}</span>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          </PlacesAutocomplete>
          <p className="pPick-up"></p>
          <p className="pPick" style={{ marginTop: "30px" }}>
            {" "}
            <img
              src="Image/mapgps.svg"
              className="img-fluid"
              style={{ marginRight: "15px" }}
            ></img>
            Drop Off Location
          </p>
          <PlacesAutocomplete
          value={addressDrop}
          onChange={setAddressDrop}
          onSelect={handleSelectDrop}
          searchOptions={searchOptions}
        >
          {({
            getInputProps,
            suggestions,
