      <div className="map-container">
        <Map
          google={this.props.google}
          className={"map"}
          zoom={14}
          initialCenter={{
            lat: 14.5995,
            lng: 120.9842,
          }}
         
        />
      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: "AIzaSyDzzi_VBcf2Oef6LTViLU767UPNHlnIze4",
  libraries: [],
})(MapContainer);
