import React, { Component, useState } from "react";
import axios from "axios";

export class register extends Component {
  state = {
    fname: "",
    lname: "",
    mname: "",
    profile: "",
    email: "",
    address: "asd",
    city: "asd",
    state: "asd",
    country: "asd",
    zip: "12312",
    password: "",
  };

  constructor(props) {
    super(props);

    this.fname = this.fname.bind(this);
    this.lname = this.lname.bind(this);
    this.mname = this.mname.bind(this);
    this.email = this.email.bind(this);
    this.password = this.password.bind(this);
  }

  email(event) {
    this.setState({ email: event.target.value });
  }
  lname(event) {
    this.setState({ lname: event.target.value });
    console.log(this.state.lname);
  }
  fname(event) {
    this.setState({ fname: event.target.value });
  }
  mname(event) {
    this.setState({ mname: event.target.value });
  }
  password(event) {
    this.setState({ password: event.target.value });
  }

  login() {
    const apiUrl = "http://localhost:8000/api/auth/register";
    const data = { fname: this.fname, lname: this.lname };
    axios.post(apiUrl, data).then((result) => {
      
      if (result.message == "Successfully created user!") {
        alert("gumana");
      } else {
        alert("hindi gumana");
      }
    });
  }

  render() {
    return (
      <div>
        <div className="container">
          <div className="row">
            <div className="col-lg-12">
              <input
                type="text"
                onChange={this.fname}
                placeholder="first name"
              ></input>
              <input
                type="text"
                onChange={this.lname}
                placeholder="last name"
              ></input>
              <input
                type="text"
                onChange={this.mname}
                placeholder="middle name"
              ></input>
              <input
                type="text"
                onChange={this.email}
                placeholder="email"
              ></input>
              <input
                type="text"
                onChange={this.password}
                placeholder="password"
              ></input>
              <input
                type="button"
                value="register"
                onClick={this.login.bind(this)}
              ></input>
              <p>{this.state.lname}</p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default register;
