import React, { Component } from "react";
import Select from "react-select";

const regions = require("philippines/regions");

export class signup extends Component {
  constructor(props) {
    super(props);
    this.state = {
      regions_api: {
        value: "",
        name: "",
      },
      province_api: {
        value: "",
        name: "",
      },
      region_change: "",
    };
  }

  componentDidMount() {
    const data_regions = regions.map((d) => ({
      value: d.key,
      label: d.name,
    }));
    this.setState({ regions_api: data_regions });
  }

  HandleChangeRegion(e) {
    this.setState({region_change: "NCR"})
    const data = province
      .filter((person) => person.region === e.value)
      .map((d) => ({
        id: d.key,
        value: d.key,
        label: d.name,
      }));
    this.setState({province_api: data})
  }

  render() {
    return (
      <div>
        <Select
          options={this.state.regions_api}
          onChange={this.HandleChangeRegion}
        />
        <Select
          options={this.state.province_api}
        />
      </div>
    );
  }
}

export default signup;
