import React, { Component } from "react";
import axios from "axios";
import swal from "@sweetalert/with-react";
import Select from "react-select";

const regions = require("philippines/regions");
const province = require("philippines/provinces");
const cities = require("philippines/cities");
const customStyles = {
  control: (base, state) => ({
    ...base,
    border: "1px solid #707070",
    boxShadow: "none",
    borderRadius: "10px",
    width: "115%",
    padding: "4.5px",
    marginTop: "5px",
  }),
};

export class login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Email: "",
      Passoword: "",
      regions_api: {
        value: "",
        name: "",
      },
      province_api: {
        value: "",
        name: "",
      },
      cities_api: {
        value: "",
        name: "",
      },
      region_change: "",
      province_change: "",
    };

    this.Password = this.Password.bind(this);
    this.Email = this.Email.bind(this);
    this.login = this.login.bind(this);
  }

  componentDidMount() {
    const data_regions = regions.map((d) => ({
      value: d.key,
      label: d.name,
    }));
    this.setState({ regions_api: data_regions });
  }

  Email(event) {
    this.setState({ Email: event.target.value });
  }
  Password(event) {
    this.setState({ Password: event.target.value });
  }

  login(event) {
    fetch("http://localhost:8000/api/auth/login", {
      method: "post",
      headers: {
        Accept: "application/json, text/plain, */*",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: this.state.Email,
        password: this.state.Password,
      }),
    })
      .then((Response) => Response.json())
      .then((result) => {
        console.log(result);
        if (result.status == "error") {
          alert(result.message);
        } else if (result.status == "success") {
          alert(result.status);
        } else if (result.status == "fail") {
          alert(result.status);
        }
      });
  }

  HandleChangeRegion(e) {
    this.setState({ region_change: e.value });
    const data = province
      .filter((person) => person.region === e.value)
      .map((d) => ({
        id: d.key,
        value: d.key,
        label: d.name,
      }));
    this.setState({ province_api: data });
  }

  HandleChangeProvince(e) {
    this.setState({ province_change: e.value });
    const data = cities
      .filter((person) => person.province === e.id)
      .map((d) => ({
        value: d.name,
        label: d.name,
      }));
    this.setState({ cities_api: data });
  }

  render() {
    return (
      <>
        <div className="col-lg-6 colLogin">
          <p className="pLogin">Log-in</p>
          <div className="boxLogin">
            <input
              type="text"
              className="txt"
              placeholder="email"
              onChange={this.Email}
            ></input>
            <input
              type="password"
              className="txt"
              placeholder="password"
              onChange={this.Password}
            ></input>
          </div>
          <div className="row align-items-center">
            <div className="col-lg-6 text-center">
              <p className="pForgot">forgot password?</p>
            </div>
            <div className="col-lg-6 text-center">
              <button className="btnSubmit" onClick={this.login}>
                SUBMIT
              </button>
            </div>
          </div>
          <div className="row">
            <div className="col-lg-5">
              <hr></hr>
            </div>
            <div className="col-lg-2 text-center">
              <p className="pOr">or</p>
            </div>
            <div className="col-lg-5">
              <hr></hr>
            </div>
          </div>
          <button className="btnFacebook">
            <img
              src="Image/facebook.png"
              style={{ width: "15px", marginRight: "5px" }}
            />
            Sign-in with facebook
          </button>
          <button className="btnGoogle">
            <img
              src="Image/google.png"
              style={{ width: "15px", marginRight: "5px" }}
            />
            Sign-in with Google
          </button>
          <p className="pDont">Don't have an account?</p>
          <p
            className="pSignup"
            data-toggle="modal"
            data-target="#exampleModal"
          >
            sign-up
          </p>
        </div>
        <div
          className="modal fade"
          id="exampleModal"
          tabIndex={-1}
          role="dialog"
          aria-labelledby="exampleModalLabel"
          aria-hidden="true"
          style={{ zIndex: "99999999999999999999999999999999" }}
        >
          <div className="modal-dialog modal-lg" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">
                  JGO - Registration Form
                </h5>
              </div>
              <div className="modal-body">
                <p className="pAccount">ACCOUNT INFORMATION</p>
                <div className="row">
                  <div className="col-lg-4">
                    <p className="p1">First Name</p>
                    <input type="text" className="txt txtModal"  onChange={this.fname}></input>
                  </div>
                  <div className="col-lg-4">
                    <p className="p1">Middle Name</p>
                    <input type="text" className="txt txtModal" onChange = {this.mname}></input>
                  </div>
                  <div className="col-lg-4">
                    <p className="p1">Last Name</p>
                    <input type="text" className="txt txtModal"  onChange={this.lname}></input>
                  </div>
                </div>
                <div className="row" style={{ marginTop: "5px" }}>
                  <div className="col-lg-4">
                    <p className="p1">Address 1</p>
                    <input
                      type="text"
                      className="txt txtModal"
                      placeholder="House/Unit Number, Building Name, Street Name"  onChange={this.address}
                    ></input>
                  </div>
                  <div className="col-lg-4">
                    <p className="p1">Region</p>
                    <Select
                      options={this.state.regions_api}
                      onChange={this.HandleChangeRegion.bind(this)}
                      styles={customStyles}
                    />
                  </div>
                  <div className="col-lg-4">
                    <p className="p1">Province</p>
                    <Select
                      options={this.state.province_api}
                      onChange={this.HandleChangeProvince.bind(this)}
                      styles={customStyles}
                    />
                  </div>
                </div>
                <div className="row" style={{ marginTop: "5px" }}>
                  <div className="col-lg-4">
                    <p className="p1">City/Municipality</p>
                    <Select
                      options={this.state.cities_api}
                      styles={customStyles}
                      onChange={this.city}
                    />
                  </div>
                  <div className="col-lg-4">
                    <p className="p1">Postal</p>
                    <input type="text" className="txt txtModal"  onChange={this.zip}></input>
                  </div>
                  <div className="col-lg-4">
                    <p className="p1" >Country</p>
                    <input
                      type="text"
                      className="txt txtModal"
                      value="Philippines"
                      disabled
                      onChange={this.country}
                    ></input>
                  </div>
                </div>
                <div className="row" style = {{marginTop: "5px"}}>
                  <div className="col-lg-6">
                    <p className="p1">Email Address</p>
                    <input type="text" className="txt txtModal"></input>
                  </div>
                  <div className="col-lg-6">
                    <p className="p1">Mobile Number</p>
                    <input type="text" className="txt txtModal"></input>
                  </div>
                </div>
                <div className="row">
                  <div className="col-lg-6">
                    <p className="p1">Password</p>
                    <input type="password" className="txt txtModal"></input>
                  </div>
                  <div className="col-lg-6">
                    <p className="p1">Confirm Password</p>
                    <input type="password" className="txt txtModal"></input>
                  </div>
                </div>
                <div className="row">
                  <div className="col-lg-6">
                    <p className="p1">Driver Lisence (Photo)</p>
                    <div
                      style={{
                        border: "1px solid #707070",
                        borderRadius: "10px",
                        padding: "5px 8px",
                        marginTop: "5px",
                        position: "relative",
                      }}
                    >
                      <label for="file-upload" className="custom-file-upload">
                        <i class="fa fa-cloud-upload"></i> Choose File
                      </label>
                      <input
                        id="file-upload"
                        type="file"
                        accept=".jpg, .png, .jpeg|image"
                      />
                    </div>
                    <p className="pImage">
                      Note: Only .jpg, .jpeg and .png files are allowed
                    </p>
                  </div>
                </div>
              </div>
              <div className="modal-footer text-center mx-auto d-flex">
                <button type="button" className="btnSubmitModal">
                  Submit
                </button>
              </div>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default login;
